create table sdc_calendar
(
    id          int(10) auto_increment comment 'Event ID',
    title       varchar(100) collate utf8mb4_unicode_ci            not null comment 'Event Title',
    description text collate utf8mb4_unicode_ci                    not null comment 'Event Description',
    start       datetime                                           null comment 'Event Date Time start',
    end         datetime                                           null comment 'Event Date Time End',
    allDay      varchar(1) collate utf8mb4_unicode_ci   default '' not null comment 'All day Event',
    calendar    varchar(100) collate utf8mb4_unicode_ci default '' not null comment 'Event''s Background Color',
    url         varchar(100) collate utf8mb4_unicode_ci default '' not null comment 'Event''s URL',
    user_id     int(10)                                            not null comment 'Event''s Ovner ID',
    freq        varchar(50)                                        null comment 'Event Repeat Freq String',
    tzid        varchar(100)                                       null comment 'Event''s Timezone',
    count       int                                                null comment 'Event''s Repeats Count',
    `interval`  int                                                null comment 'Event''s Repeating Interval',
    dtstart     datetime                                           null comment 'Event''s Repeating Date Time Start',
    until       datetime                                           null comment 'Event''s Repeating Date Time End',
    bymonth     varchar(50)                                        null comment 'Event''s Repeating by month array string',
    byweekday   varchar(50)                                        null comment 'Event''s Repeating by week day array string',
    constraint id
        unique (id)
)
    comment 'Events Fullcalendar Module';

alter table sdc_calendar
    add primary key (id);

